<?php extract($data); $is_todays_prize = date('Y-m-d') == $prize['date']; ?><div id="prize" class="frame"><? if (@$prize): ?><h3 class="account">Welcome <a href="/profile"></a>, <a class="logout">logout</a></h3><h3 class="prize_today"><?= date('Y') == date('Y', strtotime($prize['date'])) ? date( "F j", strtotime($prize['date'])) : date( "F j, Y", strtotime($prize['date'])); ?><? if ($is_todays_prize) echo '&nbsp;| <span>Win today’s prize</span>'; ?></h3><div class="prize"><img src="<?= $prize['img1'] ?>" data-img2="<?= @$prize['img2'] ?>" data-img3="<?= @$prize['img3'] ?>"/><div class="info"><h1><?= $prize['title'] ?></h1><p data-desc2="<?= safeAttr(@$prize['desc2']) ?>" data-desc3="<?= safeAttr(@$prize['desc3']) ?>"><?= $prize['desc1'] ?></p><div class="alert"></div><?php if ($is_todays_prize): ?><form id="prize_form" class="submit" action="/api/enter" method="POST"><input type="submit" value="Enter Now"/><span class="loader"></span></form><?php else: ?><a href="/">Back to today’s prize</a><?php endif ?><p class="legal">Prizes are shared across June Media sites. <span>See <a href="/rules" target="_blank">Official Rules</a>.</span></p></div></div><? else: ?><h3>No prize for this date.</h3><? endif; ?></div>